import React from "react";

let isLoggedIn = false;

const checkCondition = () => {
  if(isLoggedIn){
    return(
      <h1>Hello there...</h1>
    );
  } else {
    return(
      <form className="form">
          <input type="text" placeholder="Username" />
          <input type="password" placeholder="Password" />
          <button type="submit">Login</button>
      </form>
    );
  }
}


const App = () => {
  return (
    <div className="container">
      {checkCondition()}
    </div>
  );
}

export default App;
