import React from "react";
import Login from './Login';
import Input from './Input';

let isLoggedIn = false;

const checkCondition = () => {
  if(isLoggedIn){
    return(
      Input()
    );
  } else {
    return(
      <Login />
    );
  }
}


const App = () => {
  return (
    <div className="container">
      {checkCondition()}
    </div>
  );
}

export default App;
